CREATE DATABASE baza;
USE baza;
CREATE TABLE uzytkownicy ( uID int NOT NULL PRIMARY KEY AUTO_INCREMENT, email varchar(255) NOT NULL, haslo varchar(255) NOT NULL);
CREATE TABLE magazyny( mID int NOT NULL PRIMARY KEY AUTO_INCREMENT, nazwa varchar(255) NOT NULL, adres varchar(255) NOT NULL, telefon varchar(255)  NOT NULL, ID_u int NOT NULL, FOREIGN KEY (ID_u) REFERENCES uzytkownicy(uID));
CREATE TABLE zleceniodawcy( zID int NOT NULL PRIMARY KEY AUTO_INCREMENT, nazwa varchar(255) not null, adres varchar(255) not null, telefon varchar(255) not null, ID_u int NOT NULL, FOREIGN KEY (ID_u) REFERENCES uzytkownicy(uID));
CREATE TABLE firmy_dostawcze( fdID int NOT NULL PRIMARY KEY AUTO_INCREMENT, nazwa varchar(255) not null, adres varchar(255) not null, telefon varchar(255) not null, ID_u int NOT NULL, FOREIGN KEY (ID_u) REFERENCES uzytkownicy(uID));
CREATE TABLE zlecenia_dostawy(nr_referencyjny int NOT NULL PRIMARY KEY, towar varchar(255) not null, ilosc varchar(255) not null, skadID INT NOT NULL, dokadID INT NOT NULL, oczekiwany_czas_dostawy DATETIME, wykonawcaID int, FOREIGN KEY (wykonawcaID) REFERENCES firmy_dostawcze(fdID), FOREIGN KEY (skadID) REFERENCES magazyny(mID), FOREIGN KEY (dokadID) REFERENCES magazyny(mID));
